// client/src/pages/EditProduct.jsx

import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../auth/AuthContext.jsx';
import { useNotification } from '../components/Notification.jsx';
import API from '../api/axios.js';
import { useNavigate, useParams } from 'react-router-dom';
import { Edit, ArrowLeft, Save } from 'lucide-react';

// Mock categories for development/testing purposes
const mockCategories = [
    { _id: 'cat001', name: 'Electronics' },
    { _id: 'cat002', name: 'Accessories' },
    { _id: 'cat003', name: 'Office Supplies' },
    { _id: 'cat004', name: 'Books' },
    { _id: 'cat005', name: 'Home Goods' },
];

function EditProductPage() {
    const { token, loadingAuthState } = useAuth();
    const { showTimedMessage } = useNotification();
    const navigate = useNavigate();
    const { id } = useParams(); // Get product ID from URL

    const [name, setName] = useState('');
    const [sku, setSku] = useState('');
    const [category, setCategory] = useState('');
    const [stock, setStock] = useState(''); // Changed from quantity to stock
    const [price, setPrice] = useState('');
    const [description, setDescription] = useState('');
    const [image, setImage] = useState('');
    const [supplier, setSupplier] = useState('');
    const [loading, setLoading] = useState(true); // Start loading to fetch product data
    const [error, setError] = useState(null);
    const [categories, setCategories] = useState(mockCategories); // Initialize with mock categories

    // Fetch categories for the dropdown
    const fetchCategories = useCallback(async () => {
        if (!token) return;
        try {
            const response = await API.get('/categories', {
                headers: {
                    Authorization: `Bearer ${token}` // Include token
                }
            });
            if (response.data && response.data.length > 0) {
                setCategories(response.data);
            } else {
                console.warn('API /categories returned no data. Using mock categories.');
                showTimedMessage('No categories from API. Using default categories.', 4000, 'info');
                setCategories(mockCategories); // Fallback to mock categories
            }
        } catch (err) {
            console.error('Error fetching categories for dropdown:', err);
            showTimedMessage('Failed to load categories from API. Using default categories.', 5000, 'error');
            setCategories(mockCategories); // Fallback to mock categories on error
        }
    }, [token, showTimedMessage]);

    // Fetch single product for the edit form
    const fetchProduct = useCallback(async () => {
        if (!token || !id) {
            setLoading(false);
            return;
        }
        setLoading(true);
        setError(null);
        try {
            const response = await API.get(`/products/${id}`, {
                headers: {
                    Authorization: `Bearer ${token}` // Include token
                }
            });
            setName(response.data.name);
            setSku(response.data.sku || '');
            setCategory(response.data.category);
            setStock(response.data.stock.toString()); // Changed from quantity to stock
            setPrice(response.data.price.toString());
            setDescription(response.data.description);
            setImage(response.data.image || '');
            setSupplier(response.data.supplier || '');
        } catch (err) {
            console.error('Error fetching product:', err);
            setError(err.response?.data?.message || 'Failed to load product.');
            showTimedMessage(err.response?.data?.message || 'Failed to load product.', 5000, 'error');
            if (err.response?.status === 404 || err.response?.status === 403) {
                navigate('/products'); // Redirect if product not found or unauthorized
            }
        } finally {
            setLoading(false);
        }
    }, [id, token, navigate, showTimedMessage]);

    useEffect(() => {
        if (!loadingAuthState) {
            fetchCategories();
            fetchProduct();
        }
    }, [loadingAuthState, fetchCategories, fetchProduct]);

    // Set default category when categories are loaded or updated, if current category is not set
    useEffect(() => {
        if (categories.length > 0 && !category) {
            const productCategoryExistsInFetched = categories.some(cat => cat.name === category);
            if (!productCategoryExistsInFetched) {
                 setCategory(categories[0].name);
            }
        }
    }, [categories, category]);


    const handleSubmit = useCallback(async (e) => {
        e.preventDefault();
        if (!token) {
            showTimedMessage('You must be logged in to edit a product.', 3000);
            return;
        }
        // Validate required fields including 'stock' and 'supplier'
        if (!name.trim() || !category || stock === '' || price === '' || !description.trim() || !supplier.trim()) {
            showTimedMessage('Please fill all required fields.', 3000);
            return;
        }

        setLoading(true);
        setError(null);

        try {
            const updatedProduct = {
                name: name.trim(),
                sku: sku.trim(),
                category: category,
                stock: parseInt(stock), // Changed from quantity to stock
                price: parseFloat(price),
                description: description.trim(),
                image: image.trim() || 'https://placehold.co/600x400/cccccc/ffffff?text=No+Image',
                supplier: supplier.trim()
            };
            await API.put(`/products/${id}`, updatedProduct, {
                headers: {
                    Authorization: `Bearer ${token}` // Include token
                }
            });
            showTimedMessage('Product updated successfully!', 3000, 'success');
            navigate('/products'); // Redirect to product list
        } catch (err) {
            console.error('Error updating product:', err);
            setError(err.response?.data?.message || 'Failed to update product.');
            showTimedMessage(err.response?.data?.message || 'Failed to update product.', 5000, 'error');
        } finally {
            setLoading(false);
        }
    }, [id, name, sku, category, stock, price, description, image, supplier, token, navigate, showTimedMessage]); // Updated dependencies

    if (loadingAuthState || loading) {
        return (
            <div className="flex items-center justify-center py-10 bg-white dark:bg-gray-800 rounded-lg shadow-md transition-colors duration-300">
                <p className="text-indigo-600 dark:text-indigo-300">Loading product data...</p>
            </div>
        );
    }

    if (error && !name) { // Only show error if we failed to load data initially
        return (
            <div className="flex items-center justify-center py-10 bg-white dark:bg-gray-800 rounded-lg shadow-md transition-colors duration-300">
                <p className="text-red-600 dark:text-red-300">{error}</p>
            </div>
        );
    }

    return (
        <div className="p-6 bg-white rounded-lg shadow-md dark:bg-gray-800 dark:shadow-none transition-colors duration-300">
            <div className="flex items-center mb-6">
                <button
                    onClick={() => navigate('/products')}
                    className="mr-3 p-2 rounded-full bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-200 hover:bg-gray-300 dark:hover:bg-gray-600 transition-colors duration-200"
                    title="Back to Products"
                >
                    <ArrowLeft className="w-5 h-5" />
                </button>
                <h2 className="text-3xl font-bold text-indigo-700 flex items-center dark:text-indigo-300">
                    <Edit className="w-8 h-8 mr-3 text-purple-500 dark:text-purple-300" />
                    Edit Product
                </h2>
            </div>

            {error && (
                <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg relative mb-4 dark:bg-red-900 dark:text-red-200">
                    <strong className="font-bold">Error!</strong>
                    <span className="block sm:inline"> {error}</span>
                </div>
            )}

            <form onSubmit={handleSubmit} className="space-y-6">
                <div>
                    <label htmlFor="name" className="block text-sm font-medium text-gray-700 dark:text-gray-200 mb-1">Product Name</label>
                    <input
                        type="text"
                        id="name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        placeholder="e.g., Laptop, Smartphone"
                        required
                        className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-200
                                 bg-white dark:bg-gray-700 text-gray-900 dark:text-white placeholder-gray-500 dark:placeholder-gray-400"
                    />
                </div>
                <div>
                    <label htmlFor="sku" className="block text-sm font-medium text-gray-700 dark:text-gray-200 mb-1">SKU (Optional)</label>
                    <input
                        type="text"
                        id="sku"
                        value={sku}
                        onChange={(e) => setSku(e.target.value)}
                        placeholder="e.g., ABC-123, XYZ-456"
                        className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-200
                                 bg-white dark:bg-gray-700 text-gray-900 dark:text-white placeholder-gray-500 dark:placeholder-gray-400"
                    />
                </div>
                <div>
                    <label htmlFor="category" className="block text-sm font-medium text-gray-700 dark:text-gray-200 mb-1">Category</label>
                    <select
                        id="category"
                        value={category}
                        onChange={(e) => setCategory(e.target.value)}
                        required
                        className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-200
                                 bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
                    >
                        {categories.length > 0 ? (
                            categories.map((cat) => (
                                <option key={cat._id} value={cat.name}>{cat.name}</option>
                            ))
                        ) : (
                            <option value="">Loading categories...</option>
                        )}
                    </select>
                </div>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                        <label htmlFor="stock" className="block text-sm font-medium text-gray-700 dark:text-gray-200 mb-1">Stock</label> {/* Changed label from Quantity to Stock */}
                        <input
                            type="number"
                            id="stock" // Changed id from quantity to stock
                            value={stock}
                            onChange={(e) => setStock(e.target.value)} // Changed setQuantity to setStock
                            placeholder="e.g., 100"
                            required
                            min="0"
                            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-200
                                     bg-white dark:bg-gray-700 text-gray-900 dark:text-white placeholder-gray-500 dark:placeholder-gray-400"
                        />
                    </div>
                    <div>
                        <label htmlFor="price" className="block text-sm font-medium text-gray-700 dark:text-gray-200 mb-1">Price ($)</label>
                        <input
                            type="number"
                            id="price"
                            value={price}
                            onChange={(e) => setPrice(e.target.value)}
                            placeholder="e.g., 599.99"
                            required
                            min="0"
                            step="0.01"
                            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-200
                                     bg-white dark:bg-gray-700 text-gray-900 dark:text-white placeholder-gray-500 dark:placeholder-gray-400"
                        />
                    </div>
                </div>
                <div>
                    <label htmlFor="supplier" className="block text-sm font-medium text-gray-700 dark:text-gray-200 mb-1">Supplier</label>
                    <input
                        type="text"
                        id="supplier"
                        value={supplier}
                        onChange={(e) => setSupplier(e.target.value)}
                        placeholder="e.g., Tech Gadgets Inc."
                        required
                        className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-200
                                 bg-white dark:bg-gray-700 text-gray-900 dark:text-white placeholder-gray-500 dark:placeholder-gray-400"
                    />
                </div>
                <div>
                    <label htmlFor="description" className="block text-sm font-medium text-gray-700 dark:text-gray-200 mb-1">Description</label>
                    <textarea
                        id="description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        placeholder="Detailed description of the product"
                        required
                        rows="4"
                        className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-200
                                 bg-white dark:bg-gray-700 text-gray-900 dark:text-white placeholder-gray-500 dark:placeholder-gray-400"
                    ></textarea>
                </div>
                <div>
                    <label htmlFor="image" className="block text-sm font-medium text-gray-700 dark:text-gray-200 mb-1">Image URL (Optional)</label>
                    <input
                        type="text"
                        id="image"
                        value={image}
                        onChange={(e) => setImage(e.target.value)}
                        placeholder="e.g., https://example.com/product.jpg"
                        className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-200
                                 bg-white dark:bg-gray-700 text-gray-900 dark:text-white placeholder-gray-500 dark:placeholder-gray-400"
                    />
                </div>
                <button
                    type="submit"
                    disabled={loading}
                    className="w-full flex items-center justify-center bg-indigo-600 text-white py-3 px-6 rounded-lg font-bold hover:bg-indigo-700 transition-all duration-300 shadow-md transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-indigo-400 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                    {loading ? (
                        <svg className="animate-spin h-5 w-5 mr-3 text-white" viewBox="0 0 24 24">
                            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                        </svg>
                    ) : (
                        <Save className="w-5 h-5 mr-2" />
                    )}
                    {loading ? 'Updating Product...' : 'Update Product'}
                </button>
            </form>
        </div>
    );
}

export default EditProductPage;
