// client/src/pages/Admin/AdminDashboard.jsx

import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../../auth/AuthContext.jsx';
import { useTheme } from '../../context/ThemeContext.jsx';
import { useNotification } from '../../components/Notification.jsx';
import { Link, useNavigate } from 'react-router-dom';
import {
    LayoutDashboard, Package, Users, Truck, ClipboardList, AlertTriangle, Layers, PlusCircle,
    TrendingUp, TrendingDown, DollarSign, Activity, Calendar, Bell, Search, RefreshCw, Settings,
    BarChart3, ShoppingCart, Download, CircleDotDashed, MinusCircle
} from 'lucide-react';
import {
    LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer,
    PieChart as RechartsPieChart, Pie, Cell
} from 'recharts';

// Import the new LowStockCard component
import LowStockCard from '../../components/LowStockCountCard.jsx'; // Make sure this path is correct

// --- Mock Data (DEFINED AT TOP LEVEL) ---
const mockInventoryData = [
    { month: 'Jan', stock: 2400, sales: 2000, value: 15000 },
    { month: 'Feb', stock: 2100, sales: 2200, value: 16500 },
    { month: 'Mar', stock: 2800, sales: 1800, value: 18000 },
    { month: 'Apr', stock: 2600, sales: 2400, value: 17200 },
    { month: 'May', stock: 3200, sales: 2600, value: 19800 },
    { month: 'Jun', stock: 2900, sales: 2800, value: 21500 }
];

const defaultMockCategoryData = [
    { name: 'Electronics', value: 45, color: '#3b82f6' },
    { name: 'Clothing', value: 25, color: '#06b6d4' },
    { name: 'Books', value: 15, color: '#8b5cf6' },
    { name: 'Home & Garden', value: 10, color: '#10b981' },
    { name: 'Sports', value: 5, color: '#f59e0b' }
];

const mockRecentActivities = [
    { id: 1, type: 'stock_low', message: 'iPhone 14 Pro stock is running low', time: '2 min ago', icon: AlertTriangle, color: 'text-yellow-400' },
    { id: 2, type: 'new_order', message: 'New order #12345 received', time: '5 min ago', icon: ShoppingCart, color: 'text-green-400' },
    { id: 3, type: 'stock_out', message: 'Samsung Galaxy S23 is out of stock', time: '10 min ago', icon: Layers, color: 'text-red-400' },
    { id: 4, type: 'supplier', message: 'New supplier "Tech Solutions" added', time: '1 hour ago', icon: Truck, color: 'text-blue-400' },
    { id: 5, type: 'user', message: 'New user "John Doe" registered', time: '2 hours ago', icon: Users, color: 'text-purple-400' }
];

const defaultMockTopProducts = [
    { id: 1, name: 'iPhone 14 Pro', sales: 245, trend: 'up', change: '+12%' },
    { id: 2, name: 'MacBook Air M2', sales: 189, trend: 'up', change: '+8%' },
    { id: 3, name: 'Samsung Galaxy S23', sales: 156, trend: 'down', change: '-3%' },
    { id: 4, name: 'Dell XPS 13', sales: 134, trend: 'up', change: '+15%' },
    { id: 5, name: 'iPad Pro', sales: 98, trend: 'up', change: '+6%' }
];

// Base mock data for low/out of stock if no real product data
const baseOutOfStockProducts = [
    { id: 'mock-oos-1', name: 'Mock Wireless Headphones', sku: 'MWH-001', lastOrder: '2023-05-10' },
    { id: 'mock-oos-2', name: 'Mock Gaming Mouse Pad', sku: 'MGMP-005', lastOrder: '2023-06-01' },
];

const baseLowStockProducts = [
    { id: 'mock-ls-1', name: 'Mock USB-C Hub', sku: 'MUCH-010', stock: 5, threshold: 10 },
    { id: 'mock-ls-2', name: 'Mock Portable SSD 1TB', sku: 'MSSD-002', stock: 8, threshold: 10 },
];


// Initial state for dashboard statistics
const initialStats = {
    totalProducts: 0,
    totalStock: 0,
    ordersToday: 0,
    revenueToday: 0,
    lowStockCount: 0,
    outOfStockCount: 0,
    totalValue: 0,
    monthlyGrowth: 0,
    activeSuppliers: 0,
    pendingOrders: 0,
    totalUsers: 0
};

function AdminDashboardPage() {
    const { user, isAuthenticated, loading: authLoading } = useAuth();
    const { theme } = useTheme();
    const { showTimedMessage } = useNotification();
    const navigate = useNavigate();

    const [dashboardData, setDashboardData] = useState({
        stats: initialStats,
        inventoryTrends: mockInventoryData,
        categoryDistribution: defaultMockCategoryData,
        recentActivities: mockRecentActivities,
        topProducts: defaultMockTopProducts,
        outOfStockProducts: [],
        lowStockProducts: [],
    });
    const [loadingData, setLoadingData] = useState(true);
    const [errorData, setErrorData] = useState(null);
    const [isRefreshing, setIsRefreshing] = useState(false);
    const [notifications, setNotifications] = useState([
        { id: 1, message: '5 items need restock', type: 'warning', read: false },
        { id: 2, message: 'Monthly report ready', type: 'info', read: false },
        { id: 3, message: 'New order from premium customer', type: 'success', read: true }
    ]);

    const fetchDashboardData = useCallback(async () => {
        setLoadingData(true);
        setErrorData(null);
        setIsRefreshing(true);

        try {
            const backendBaseUrl = 'http://localhost:5000/api';

            // Fetch all data concurrently
            const [
                productsCountResponse,
                totalStockResponse,
                ordersTodayResponse,
                revenueTodayResponse,
                allProductsResponse,
                allCategoriesResponse,
                allSuppliersResponse
            ] = await Promise.all([
                fetch(`${backendBaseUrl}/products/count`),
                fetch(`${backendBaseUrl}/products/total-stock`),
                fetch(`${backendBaseUrl}/orders/today-count`),
                fetch(`${backendBaseUrl}/orders/today-revenue`),
                fetch(`${backendBaseUrl}/products/all`),
                fetch(`${backendBaseUrl}/categories/all`),
                fetch(`${backendBaseUrl}/suppliers/all`)
            ]);

            // Process responses, with error handling and fallbacks
            let totalProductsCount = 0;
            try {
                if (!productsCountResponse.ok) throw new Error(`Products count failed: ${productsCountResponse.statusText}`);
                const data = await productsCountResponse.json();
                totalProductsCount = data.count;
            } catch (err) { console.error('Error fetching product count:', err); totalProductsCount = 0; }

            let totalStock = 0;
            try {
                if (!totalStockResponse.ok) throw new Error(`Total stock failed: ${totalStockResponse.statusText}`);
                const data = await totalStockResponse.json();
                totalStock = data.totalStock;
            } catch (err) { console.error('Error fetching total stock:', err); totalStock = 0; }

            let ordersToday = 0;
            try {
                if (!ordersTodayResponse.ok) throw new Error(`Orders today count failed: ${ordersTodayResponse.statusText}`);
                const data = await ordersTodayResponse.json();
                ordersToday = data.ordersToday;
            } catch (err) { console.error('Error fetching orders today count:', err); ordersToday = 0; }

            let revenueToday = 0;
            try {
                if (!revenueTodayResponse.ok) throw new Error(`Revenue today failed: ${revenueTodayResponse.statusText}`);
                const data = await revenueTodayResponse.json();
                revenueToday = data.revenueToday;
            } catch (err) { console.error('Error fetching revenue today:', err); revenueToday = 0; }

            // --- Product List Fetching and Debugging ---
            let allProducts = [];
            try {
                console.log('DEBUG FE: Attempting to fetch all products from:', `${backendBaseUrl}/products/all`);
                if (!allProductsResponse.ok) {
                    console.error('DEBUG FE: allProductsResponse NOT OK. Status:', allProductsResponse.status, 'StatusText:', allProductsResponse.statusText);
                    throw new Error(`All products list failed: ${allProductsResponse.statusText}`);
                }
                allProducts = await allProductsResponse.json();
                console.log('DEBUG FE: Raw allProducts response:', allProducts); // Log the raw response
                if (!Array.isArray(allProducts)) {
                    console.error('DEBUG FE: allProducts is not an array. Received:', typeof allProducts, allProducts);
                    allProducts = []; // Ensure it's an array for safety
                }
                if (allProducts.length === 0) {
                    console.warn("DEBUG FE: No products from API. Using default mock data for top products, out of stock, and low stock.");
                } else {
                    console.log(`DEBUG FE: Successfully fetched ${allProducts.length} products.`);
                }
            } catch (err) {
                console.error("DEBUG FE: Error fetching all products list:", err);
                console.warn("DEBUG FE: Falling back to mock data for top products, out of stock, and low stock.");
                allProducts = []; // Fallback to empty array to use mocks
            }
            // --- End Product List Debugging ---


            let allCategories = [];
            try {
                const allCategoriesResponse = await fetch(`${backendBaseUrl}/categories/all`);
                if (!allCategoriesResponse.ok) throw new Error(`All categories list failed: ${allCategoriesResponse.statusText}`);
                allCategories = await allCategoriesResponse.json();
                if (allCategories.length === 0) console.warn("No categories from API. Using default mock data for category distribution.");
            } catch (err) { console.error("Error fetching all categories list:", err); console.warn("Falling back to mock data for category distribution."); }

            let allSuppliers = [];
            try {
                const allSuppliersResponse = await fetch(`${backendBaseUrl}/suppliers/all`);
                if (!allSuppliersResponse.ok) throw new Error(`All suppliers list failed: ${allSuppliersResponse.statusText}`);
                allSuppliers = await allSuppliersResponse.json();
                if (allSuppliers.length === 0) console.warn("No suppliers from API. Using default mock data for active suppliers.");
            } catch (err) { console.error("Error fetching all suppliers list:", err); console.warn("Falling back to mock data for active suppliers count."); }


            // --- Process fetched data and combine with mocks for dashboard state ---
            const categoryDistributionData = allCategories.length > 0
                ? allCategories.map(cat => ({
                    name: cat.name,
                    value: Math.floor(Math.random() * 20) + 5,
                    color: '#' + Math.floor(Math.random()*16777215).toString(16)
                }))
                : defaultMockCategoryData;

            // For Top Products: Use fetched products, or fallback to mock
            // Sort by stock (descending) to show "top" products by availability
            const topProductsData = allProducts.length > 0
                ? allProducts.sort((a, b) => b.stock - a.stock).slice(0, 5).map((p, index) => ({
                    id: p._id,
                    name: p.name,
                    sales: Math.floor(Math.random() * 300) + 50, // Mock sales for now
                    trend: index % 2 === 0 ? 'up' : 'down', // Mock trend for now
                    change: `+${Math.floor(Math.random() * 10)}%` // Mock change for now
                }))
                : defaultMockTopProducts;

            // Determine Out of Stock and Low Stock products from fetched data
            const outOfStockProducts = allProducts.filter(p => p.stock === 0).map(p => ({
                id: p._id,
                name: p.name,
                sku: p.sku,
                lastOrder: p.updatedAt ? new Date(p.updatedAt).toISOString().split('T')[0] : 'N/A' // Use updatedAt as a proxy for last activity
            }));

            // IMPORTANT: This LOW_STOCK_THRESHOLD should ideally come from backend config or product/category specific thresholds
            // For now, it's a client-side constant.
            const LOW_STOCK_THRESHOLD = 10;
            const lowStockProducts = allProducts.filter(p => p.stock > 0 && p.stock <= LOW_STOCK_THRESHOLD).map(p => ({
                id: p._id,
                name: p.name,
                sku: p.sku,
                stock: p.stock,
                threshold: LOW_STOCK_THRESHOLD
            }));


            const activeSuppliersCount = allSuppliers.length > 0 ? allSuppliers.length : 34;

            const currentLowStockCount = lowStockProducts.length;
            const currentOutOfStockCount = outOfStockProducts.length;
            const currentTotalValue = allProducts.reduce((acc, p) => acc + (p.stock * p.price), 0); // Calculate total value from real data
            const currentMonthlyGrowth = (10 + Math.random() * 5).toFixed(1); // Still mock

            const updatedStats = {
                totalProducts: totalProductsCount,
                totalStock: totalStock,
                ordersToday: ordersToday,
                revenueToday: parseFloat(revenueToday),
                lowStockCount: currentLowStockCount, // Now uses real data count
                outOfStockCount: currentOutOfStockCount, // Now uses real data count
                totalValue: currentTotalValue, // Now uses real data
                monthlyGrowth: parseFloat(currentMonthlyGrowth),
                activeSuppliers: activeSuppliersCount,
                pendingOrders: 156, // Still mock
                totalUsers: 89 // Still mock
            };

            setDashboardData({
                stats: updatedStats,
                inventoryTrends: mockInventoryData,
                categoryDistribution: categoryDistributionData,
                recentActivities: mockRecentActivities,
                topProducts: topProductsData,
                outOfStockProducts: outOfStockProducts.length > 0 ? outOfStockProducts : baseOutOfStockProducts, // Fallback if no real OOS products
                lowStockProducts: lowStockProducts.length > 0 ? lowStockProducts : baseLowStockProducts,     // Fallback if no real LS products
            });

            showTimedMessage('Dashboard data loaded successfully!', 2000, 'success');
        } catch (err) {
            console.error('Final Dashboard Data Fetch Error (Caught in Frontend):', err);
            setErrorData(`Failed to load dashboard data: ${err.message}. Please check your backend server and network connection.`);
            showTimedMessage('Failed to load dashboard data. See console for details.', 5000, 'error');
        } finally {
            setLoadingData(false);
            setIsRefreshing(false);
        }
    }, [showTimedMessage]);

    useEffect(() => {
        if (isAuthenticated) {
            fetchDashboardData();
        }
    }, [isAuthenticated, fetchDashboardData]);

    useEffect(() => {
        if (!authLoading && !isAuthenticated) {
            navigate('/login');
        }
    }, [isAuthenticated, authLoading, navigate]);

    const StatCard = ({ Icon, title, value, subtitle, gradientFrom, gradientTo, trend, trendValue, isLoading }) => (
        <div className="bg-white bg-opacity-90 dark:bg-gray-700 dark:bg-opacity-80 rounded-2xl p-6 shadow-xl border border-gray-200 dark:border-gray-600 hover:shadow-2xl transition-all duration-300">
            <div className="flex items-center justify-between">
                <div className="flex items-center space-x-4">
                    <div className={`p-3 rounded-xl bg-gradient-to-r ${gradientFrom} ${gradientTo} shadow-lg`}>
                        <Icon className="w-6 h-6 text-white" />
                    </div>
                    <div>
                        <p className="text-sm font-medium text-gray-600 dark:text-blue-200">{title}</p>
                        {isLoading ? (
                            <div className="h-7 w-24 bg-gray-200 dark:bg-gray-600 rounded animate-pulse"></div>
                        ) : (
                            <h3 className="text-2xl font-bold text-gray-900 dark:text-white">{value}</h3>
                        )}
                        {subtitle && <p className="text-xs text-gray-500 dark:text-blue-300">{subtitle}</p>}
                    </div>
                </div>
                {trend && !isLoading && (
                    <div className={`flex items-center space-x-1 ${trend === 'up' ? 'text-green-500' : 'text-red-500'}`}>
                        {trend === 'up' ? <TrendingUp className="w-4 h-4" /> : <TrendingDown className="w-4 h-4" />}
                        <span className="text-sm font-semibold">{trendValue}</span>
                    </div>
                )}
            </div>
        </div>
    );

    const ActionButton = ({ to, children, gradientFrom = 'from-blue-600', gradientTo = 'to-cyan-600', icon: Icon }) => (
        <Link to={to} className={`bg-gradient-to-r ${gradientFrom} ${gradientTo} text-white px-6 py-3 rounded-xl shadow-lg hover:shadow-xl transform hover:scale-105 transition-all duration-200 text-sm font-semibold flex items-center justify-center gap-2`}>
            {Icon && <Icon className="w-4 h-4" />}
            {children}
        </Link>
    );

    if (authLoading) {
        return (
            <div className="flex items-center justify-center min-h-screen bg-gray-100 dark:bg-gray-900 transition-colors duration-300">
                <div className="w-8 h-8 border-4 border-blue-500 border-t-transparent rounded-full animate-spin"></div>
                <span className="ml-3 text-lg font-medium text-blue-600 dark:text-blue-300">Loading application...</span>
            </div>
        );
    }

    if (!isAuthenticated) {
        return null;
    }

    return (
        <div className={`relative p-6 rounded-xl shadow-lg transition-colors duration-300 mt-6
            ${theme === 'dark' ? 'bg-gradient-to-br from-slate-900 via-blue-900 to-slate-800' : 'bg-white'}
            ${theme === 'dark' ? 'shadow-2xl' : 'shadow-lg'}
            `}>
            {theme === 'dark' && (
                <div className="absolute inset-0 opacity-20 rounded-xl" style={{
                    backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23ffffff' fill-opacity='0.03'%3E%3Cpath d='M36 34v-4h-2v4h-4v2h4v4h2v-4h4v-2h-4zm0-30V0h-2v4h-4v2h4v4h2V6h4V4h-4zM6 34v-4H4v4H0v2h4v4h2v-4h4v-2H6zM6 4V0H4v4H0v2h4v4h2V6h4V4H6z'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")`
                }}></div>
            )}

            <div className="relative z-10">
                {/* Dashboard Header Section */}
                <div className={`flex flex-col md:flex-row justify-between items-start md:items-center mb-6 p-4 rounded-lg border transition-colors duration-300
                    ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-blue-50 border-blue-200'}
                `}>
                    <div className="flex items-center space-x-4">
                        <div className="bg-gradient-to-r from-blue-500 to-cyan-500 p-3 rounded-xl shadow-lg">
                            <LayoutDashboard className="w-8 h-8 text-white" />
                        </div>
                        <div>
                            <h1 className="text-3xl font-bold text-blue-800 dark:text-white">
                                Inventory & Stock Dashboard
                            </h1>
                            {/* Updated welcome message */}
                            <p className="text-blue-700 mt-1 dark:text-blue-200">
                                Welcome back, <span className="font-semibold">{user?.name || user?.email}!</span> Get a clear overview of your product inventory.
                            </p>
                        </div>
                    </div>

                    <div className="flex items-center gap-3 mt-4 md:mt-0">
                        {/* Notifications Icon and Count */}
                        <div className="relative">
                            <Bell className="w-6 h-6 text-blue-600 dark:text-blue-200 hover:text-blue-800 dark:hover:text-white cursor-pointer transition-colors" />
                            <span className="absolute -top-2 -right-2 bg-gradient-to-r from-red-500 to-pink-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
                                {notifications.filter(n => !n.read).length}
                            </span>
                        </div>

                        {/* Refresh Button with spinner */}
                        <button
                            onClick={fetchDashboardData}
                            disabled={isRefreshing}
                            className={`p-2 rounded-xl border transition-all duration-200 ${isRefreshing ? 'animate-spin' : ''}
                                ${theme === 'dark' ? 'bg-gray-800 border-gray-600 hover:bg-gray-700' : 'bg-gray-100 border-gray-300 hover:bg-gray-200'}
                                disabled:opacity-60 disabled:cursor-not-allowed
                            `}
                        >
                            <RefreshCw className="w-5 h-5 text-gray-600 dark:text-blue-200" />
                        </button>

                        {/* Search Input */}
                        <div className={`flex items-center gap-2 rounded-xl px-4 py-2 border transition-colors duration-300
                            ${theme === 'dark' ? 'bg-gray-800 border-gray-600' : 'bg-gray-100 border-gray-300'}
                        `}>
                            <Search className="w-4 h-4 text-gray-600 dark:text-blue-200" />
                            <input
                                type="text"
                                placeholder="Search inventory..."
                                className="bg-transparent border-none outline-none text-sm text-gray-800 placeholder-gray-500 dark:text-white dark:placeholder-blue-300 w-48"
                            />
                        </div>
                    </div>
                </div>

                {/* Error Message Display */}
                {errorData && (
                    <div className="p-4 mb-6 bg-red-100 dark:bg-red-900 border border-red-400 dark:border-red-700 text-red-700 dark:text-red-300 rounded-lg text-center">
                        <p>{errorData}</p>
                    </div>
                )}

                {/* Key Metrics Section (Summary Cards) */}
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
                    <StatCard
                        Icon={Package}
                        title="Total Products"
                        value={loadingData ? '' : dashboardData.stats.totalProducts.toLocaleString()}
                        gradientFrom="from-blue-500"
                        gradientTo="to-cyan-500"
                        trend="up"
                        trendValue="+5.2%" // This trend is still mocked
                        isLoading={loadingData}
                    />
                    <StatCard
                        Icon={Layers} // Icon for Total Stock
                        title="Total Stock"
                        value={loadingData ? '' : dashboardData.stats.totalStock.toLocaleString()}
                        gradientFrom="from-purple-500"
                        gradientTo="to-indigo-500"
                        trend="up"
                        trendValue="+3.1%"
                        isLoading={loadingData}
                    />
                    <StatCard
                        Icon={CircleDotDashed} // Icon for Orders Today
                        title="Orders Today"
                        value={loadingData ? '' : dashboardData.stats.ordersToday.toLocaleString()}
                        gradientFrom="from-orange-500"
                        gradientTo="to-yellow-500"
                        subtitle="New orders received"
                        isLoading={loadingData}
                    />
                    <StatCard
                        Icon={DollarSign}
                        title="Revenue Today"
                        value={loadingData ? '' : `$${dashboardData.stats.revenueToday.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`}
                        gradientFrom="from-green-500"
                        gradientTo="to-emerald-500"
                        trend="up"
                        trendValue="+12.5%"
                        isLoading={loadingData}
                    />
                    {/*  Low Stock Card - Added here */}
                    {user?.role === 'admin' && <LowStockCard />}
                </div>

                {/* Charts and Analytics Section */}
                <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
                    {/* Inventory Trends Line Chart */}
                    <div className={`lg:col-span-2 backdrop-blur-sm rounded-2xl shadow-xl p-6 border transition-colors duration-300
                        ${theme === 'dark' ? 'bg-white bg-opacity-10 border-white border-opacity-20' : 'bg-white border-gray-200'}
                    `}>
                        <div className="flex items-center justify-between mb-6">
                            <h3 className="text-lg font-semibold text-gray-800 dark:text-white">Inventory Trends</h3>
                            <div className="flex items-center gap-2">
                                <select className={`text-sm rounded-lg px-3 py-1 border focus:outline-none focus:ring-2 focus:ring-blue-500 transition-colors duration-300
                                    ${theme === 'dark' ? 'bg-gray-800 border-gray-600 text-white' : 'bg-gray-100 border-gray-300 text-gray-800'}
                                `}>
                                    <option className="text-gray-800 dark:text-gray-200">Last 6 months</option>
                                    <option className="text-gray-800 dark:text-gray-200">Last 12 months</option>
                                    <option className="text-gray-800 dark:text-gray-200">This year</option>
                                </select>
                            </div>
                        </div>

                        {loadingData ? (
                            <div className="flex items-center justify-center h-64">
                                <div className="w-10 h-10 border-4 border-blue-400 border-t-transparent rounded-full animate-spin"></div>
                                <span className="ml-3 text-gray-600 dark:text-blue-200">Loading chart...</span>
                            </div>
                        ) : (
                            <ResponsiveContainer width="100%" height={300}>
                                <LineChart data={dashboardData.inventoryTrends}>
                                    <CartesianGrid strokeDasharray="3 3" stroke={theme === 'dark' ? 'rgba(255,255,255,0.1)' : '#e5e7eb'} />
                                    <XAxis dataKey="month" stroke={theme === 'dark' ? '#cbd5e1' : '#6b7280'} />
                                    <YAxis stroke={theme === 'dark' ? '#cbd5e1' : '#6b7280'} />
                                    <Tooltip
                                        contentStyle={{
                                            backgroundColor: theme === 'dark' ? 'rgba(15, 23, 42, 0.9)' : 'rgba(255,255,255,0.9)',
                                            border: theme === 'dark' ? '1px solid rgba(255,255,255,0.2)' : '1px solid rgba(0,0,0,0.2)',
                                            borderRadius: '12px',
                                            boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
                                            color: theme === 'dark' ? 'white' : '#374151'
                                        }}
                                    />
                                    <Line
                                        type="monotone"
                                        dataKey="stock"
                                        stroke="#3b82f6"
                                        strokeWidth={3}
                                        dot={{ fill: '#3b82f6', strokeWidth: 2, r: 4 }}
                                        activeDot={{ r: 6, stroke: '#3b82f6', strokeWidth: 2 }}
                                    />
                                    <Line
                                        type="monotone"
                                        dataKey="sales"
                                        stroke="#06b6d4"
                                        strokeWidth={3}
                                        dot={{ fill: '#06b6d4', strokeWidth: 2, r: 4 }}
                                        activeDot={{ r: 6, stroke: '#06b6d4', strokeWidth: 2 }}
                                    />
                                </LineChart>
                            </ResponsiveContainer>
                        )}
                    </div>

                    {/* Category Distribution Pie Chart */}
                    <div className={`backdrop-blur-sm rounded-2xl shadow-xl p-6 border transition-colors duration-300
                        ${theme === 'dark' ? 'bg-white bg-opacity-10 border-white border-opacity-20' : 'bg-white border-gray-200'}
                    `}>
                        <h3 className="text-lg font-semibold text-gray-800 dark:text-white mb-6">Category Distribution</h3>
                        {loadingData ? (
                            <div className="flex items-center justify-center h-64">
                                <div className="w-10 h-10 border-4 border-blue-400 border-t-transparent rounded-full animate-spin"></div>
                                <span className="ml-3 text-gray-600 dark:text-blue-200">Loading chart...</span>
                            </div>
                        ) : (
                            <ResponsiveContainer width="100%" height={300}>
                                <RechartsPieChart>
                                    <Pie
                                        data={dashboardData.categoryDistribution}
                                        cx="50%"
                                        cy="50%"
                                        outerRadius={80}
                                        dataKey="value"
                                        label={({ name, value }) => `${name}: ${value}%`}
                                        labelLine={false}
                                    >
                                        {dashboardData.categoryDistribution.map((entry, index) => (
                                            <Cell key={`cell-${index}`} fill={entry.color} />
                                        ))}
                                    </Pie>
                                    <Tooltip
                                        contentStyle={{
                                            backgroundColor: theme === 'dark' ? 'rgba(15, 23, 42, 0.9)' : 'rgba(255,255,255,0.9)',
                                            border: theme === 'dark' ? '1px solid rgba(255,255,255,0.2)' : '1px solid rgba(0,0,0,0.2)',
                                            borderRadius: '12px',
                                            boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
                                            color: theme === 'dark' ? 'white' : '#374151'
                                        }}
                                    />
                                </RechartsPieChart>
                            </ResponsiveContainer>
                        )}
                    </div>
                </div>

                {/* New sections for Out of Stock and Low Stock Products */}
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
                    {/* Out of Stock Products List */}
                    <div className={`backdrop-blur-sm rounded-2xl shadow-xl p-6 border transition-colors duration-300
                        ${theme === 'dark' ? 'bg-white bg-opacity-10 border-white border-opacity-20' : 'bg-white border-gray-200'}
                    `}>
                        <div className="flex items-center justify-between mb-4">
                            <h3 className="text-lg font-semibold text-gray-800 dark:text-white">Out of Stock Products</h3>
                            <Layers className="w-5 h-5 text-red-500" />
                        </div>

                        {loadingData ? (
                            <div className="space-y-3">
                                {[...Array(3)].map((_, i) => (
                                    <div key={i} className="flex items-center justify-between p-3 rounded-lg bg-gray-100 dark:bg-gray-600 animate-pulse">
                                        <div className="flex-1 space-y-1">
                                            <div className="h-4 bg-gray-300 dark:bg-gray-500 rounded w-3/4"></div>
                                            <div className="h-3 bg-gray-200 dark:bg-gray-500 rounded w-1/2"></div>
                                        </div>
                                        <div className="w-16 h-4 bg-gray-300 dark:bg-gray-500 rounded"></div>
                                    </div>
                                ))}
                            </div>
                        ) : dashboardData.outOfStockProducts.length === 0 ? (
                            <p className="text-center text-gray-600 dark:text-gray-300 py-4">No products currently out of stock. Good job!</p>
                        ) : (
                            <div className="space-y-3">
                                {dashboardData.outOfStockProducts.map((product) => (
                                    <div key={product.id} className={`flex items-center justify-between p-3 rounded-lg transition-colors
                                        ${theme === 'dark' ? 'bg-white bg-opacity-5' : 'bg-gray-50'}
                                        hover:bg-gray-100 dark:hover:bg-gray-600
                                    `}>
                                        <div className="flex-1">
                                            <p className="text-sm font-medium text-gray-800 dark:text-white">{product.name}</p>
                                            <p className="text-xs text-gray-500 dark:text-blue-300">SKU: {product.sku}</p>
                                        </div>
                                        <span className="text-xs text-red-500 font-semibold">OUT OF STOCK</span>
                                    </div>
                                ))}
                            </div>
                        )}
                        <button className="w-full mt-4 text-sm text-blue-600 hover:text-blue-800 font-medium transition-colors dark:text-blue-300 dark:hover:text-white">
                            View All Out of Stock
                        </button>
                    </div>

                    {/* Low Stock Products List */}
                    <div className={`backdrop-blur-sm rounded-2xl shadow-xl p-6 border transition-colors duration-300
                        ${theme === 'dark' ? 'bg-white bg-opacity-10 border-white border-opacity-20' : 'bg-white border-gray-200'}
                    `}>
                        <div className="flex items-center justify-between mb-4">
                            <h3 className="text-lg font-semibold text-gray-800 dark:text-white">Low Stock Products</h3>
                            <MinusCircle className="w-5 h-5 text-yellow-500" />
                        </div>

                        {loadingData ? (
                            <div className="space-y-3">
                                {[...Array(4)].map((_, i) => (
                                    <div key={i} className="flex items-center justify-between p-3 rounded-lg bg-gray-100 dark:bg-gray-600 animate-pulse">
                                        <div className="flex-1 space-y-1">
                                            <div className="h-4 bg-gray-300 dark:bg-gray-500 rounded w-3/4"></div>
                                            <div className="h-3 bg-gray-200 dark:bg-gray-500 rounded w-1/2"></div>
                                        </div>
                                        <div className="w-12 h-4 bg-gray-300 dark:bg-gray-500 rounded"></div>
                                    </div>
                                ))}
                            </div>
                        ) : dashboardData.lowStockProducts.length === 0 ? (
                            <p className="text-center text-gray-600 dark:text-gray-300 py-4">All products are adequately stocked!</p>
                        ) : (
                            <div className="space-y-3">
                                {dashboardData.lowStockProducts.map((product) => (
                                    <div key={product.id} className={`flex items-center justify-between p-3 rounded-lg transition-colors
                                        ${theme === 'dark' ? 'bg-white bg-opacity-5' : 'bg-gray-50'}
                                        hover:bg-gray-100 dark:hover:bg-gray-600
                                    `}>
                                        <div className="flex-1">
                                            <p className="text-sm font-medium text-gray-800 dark:text-white">{product.name}</p>
                                            <p className="text-xs text-gray-500 dark:text-blue-300">SKU: {product.sku}</p>
                                        </div>
                                        <span className="text-sm font-semibold text-yellow-500">Stock: {product.stock}</span>
                                    </div>
                                ))}
                            </div>
                        )}
                        <button className="w-full mt-4 text-sm text-blue-600 hover:text-blue-800 font-medium transition-colors dark:text-blue-300 dark:hover:text-white">
                            View All Low Stock
                        </button>
                    </div>
                </div>


                {/* Dashboard Grid (Recent Activity, Top Products, Quick Stats) */}
                <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
                    {/* Recent Activity List */}
                    <div className={`backdrop-blur-sm rounded-2xl shadow-xl p-6 border transition-colors duration-300
                        ${theme === 'dark' ? 'bg-white bg-opacity-10 border-white border-opacity-20' : 'bg-white border-gray-200'}
                    `}>
                        <div className="flex items-center justify-between mb-4">
                            <h3 className="text-lg font-semibold text-gray-800 dark:text-white">Recent Activity</h3>
                            <Activity className="w-5 h-5 text-gray-600 dark:text-blue-200" />
                        </div>

                        {loadingData ? (
                            <div className="space-y-4">
                                {[...Array(5)].map((_, i) => (
                                    <div key={i} className="flex items-center space-x-3 p-3 rounded-lg bg-gray-100 dark:bg-gray-600 animate-pulse">
                                        <div className="w-5 h-5 rounded-full bg-gray-300 dark:bg-gray-500"></div>
                                        <div className="flex-1 space-y-2">
                                            <div className="h-4 bg-gray-300 dark:bg-gray-500 rounded w-3/4"></div>
                                            <div className="h-3 bg-gray-200 dark:bg-gray-500 rounded w-1/2"></div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <div className="space-y-4">
                                {dashboardData.recentActivities.map((activity) => (
                                    <div key={activity.id} className={`flex items-start space-x-3 p-3 rounded-lg transition-colors
                                        ${theme === 'dark' ? 'bg-white bg-opacity-5 hover:bg-opacity-10' : 'bg-gray-50 hover:bg-gray-100'}
                                    `}>
                                        <activity.icon className={`w-5 h-5 ${activity.color} mt-0.5`} />
                                        <div className="flex-1">
                                            <p className="text-sm text-gray-800 dark:text-white">{activity.message}</p>
                                            <p className="text-xs text-gray-500 mt-1 dark:text-blue-300">{activity.time}</p>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}
                        <button className="w-full mt-4 text-sm text-blue-600 hover:text-blue-800 font-medium transition-colors dark:text-blue-300 dark:hover:text-white">
                            View All Activity
                        </button>
                    </div>

                    {/* Top Products List */}
                    <div className={`backdrop-blur-sm rounded-2xl shadow-xl p-6 border transition-colors duration-300
                        ${theme === 'dark' ? 'bg-white bg-opacity-10 border-white border-opacity-20' : 'bg-white border-gray-200'}
                    `}>
                        <div className="flex items-center justify-between mb-4">
                            <h3 className="text-lg font-semibold text-gray-800 dark:text-white">Top Products</h3>
                            <BarChart3 className="w-5 h-5 text-gray-600 dark:text-blue-200" />
                        </div>

                        {loadingData ? (
                            <div className="space-y-3">
                                {[...Array(5)].map((_, i) => (
                                    <div key={i} className="flex items-center justify-between p-3 rounded-lg bg-gray-100 dark:bg-gray-600 animate-pulse">
                                        <div className="flex items-center space-x-3 w-3/4">
                                            <div className="w-6 h-4 bg-gray-300 dark:bg-gray-500 rounded"></div>
                                            <div className="space-y-1 w-full">
                                                <div className="h-4 bg-gray-300 dark:bg-gray-500 rounded w-full"></div>
                                                <div className="h-3 bg-gray-200 dark:bg-gray-500 rounded w-2/3"></div>
                                            </div>
                                        </div>
                                        <div className="w-12 h-4 bg-gray-300 dark:bg-gray-500 rounded"></div>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <div className="space-y-3">
                                {dashboardData.topProducts.map((product, index) => (
                                    <div key={product.id} className={`flex items-center justify-between p-3 rounded-lg transition-colors
                                        ${theme === 'dark' ? 'bg-white bg-opacity-5' : 'bg-gray-50'}
                                        hover:bg-gray-100 dark:hover:bg-gray-600
                                    `}>
                                        <div className="flex items-center space-x-3">
                                            <span className="text-sm font-semibold text-gray-600 w-6 dark:text-blue-300">#{index + 1}</span>
                                            <div>
                                                <p className="text-sm font-medium text-gray-800 dark:text-white">{product.name}</p>
                                                <p className="text-xs text-gray-500 dark:text-blue-300">{product.sales} sales</p>
                                            </div>
                                        </div>
                                        <div className={`flex items-center space-x-1 ${product.trend === 'up' ? 'text-green-500' : 'text-red-500'}`}>
                                            {product.trend === 'up' ? <TrendingUp className="w-4 h-4" /> : <TrendingDown className="w-4 h-4" />}
                                            <span className="text-xs font-semibold">{product.change}</span>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>

                    {/* Quick Stats (Admin-specific: Total Users) */}
                    <div className={`backdrop-blur-sm rounded-2xl shadow-xl p-6 border transition-colors duration-300
                        ${theme === 'dark' ? 'bg-white bg-opacity-10 border-white border-opacity-20' : 'bg-white border-gray-200'}
                    `}>
                        <h3 className="text-lg font-semibold text-gray-800 dark:text-white mb-4">Quick Stats</h3>

                        {loadingData ? (
                            <div className="space-y-4">
                                {[...Array(3)].map((_, i) => (
                                    <div key={i} className="flex items-center justify-between p-3 rounded-lg bg-gray-100 dark:bg-gray-600 animate-pulse">
                                        <div className="w-5 h-5 rounded-full bg-gray-300 dark:bg-gray-500"></div>
                                        <div className="h-4 bg-gray-300 dark:bg-gray-500 rounded w-full"></div>
                                        <div className="w-16 h-6 bg-gray-300 dark:bg-gray-500 rounded"></div>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <div className="space-y-4">
                                <div className={`flex items-center justify-between p-3 rounded-lg
                                    ${theme === 'dark' ? 'bg-gray-600' : 'bg-blue-50'}
                                `}>
                                    <div className="flex items-center space-x-3">
                                        <Truck className="w-5 h-5 text-blue-600 dark:text-blue-300" />
                                        <span className="text-sm font-medium text-gray-800 dark:text-white">Active Suppliers</span>
                                    </div>
                                    <span className="text-lg font-bold text-blue-800 dark:text-white">{dashboardData.stats.activeSuppliers}</span>
                                </div>

                                <div className={`flex items-center justify-between p-3 rounded-lg
                                    ${theme === 'dark' ? 'bg-gray-600' : 'bg-green-50'}
                                `}>
                                    <div className="flex items-center space-x-3">
                                        <ClipboardList className="w-5 h-5 text-green-600 dark:text-green-300" />
                                        <span className="text-sm font-medium text-gray-800 dark:text-white">Pending Orders</span>
                                    </div>
                                    <span className="text-lg font-bold text-green-800 dark:text-white">{dashboardData.stats.pendingOrders}</span>
                                </div>

                                {user?.role === 'admin' && (
                                    <div className={`flex items-center justify-between p-3 rounded-lg
                                        ${theme === 'dark' ? 'bg-gray-600' : 'bg-purple-50'}
                                    `}>
                                        <div className="flex items-center space-x-3">
                                            <Users className="w-5 h-5 text-purple-600 dark:text-purple-300" />
                                            <span className="text-sm font-medium text-gray-800 dark:text-white">Total Users</span>
                                        </div>
                                        <span className="text-lg font-bold text-purple-800 dark:text-white">{dashboardData.stats.totalUsers}</span>
                                    </div>
                                )}
                            </div>
                        )}
                    </div>
                </div>

                {user?.role === 'admin' && (
                    <div className={`backdrop-blur-sm rounded-2xl shadow-xl p-6 border transition-colors duration-300
                        ${theme === 'dark' ? 'bg-white bg-opacity-10 border-white border-opacity-20' : 'bg-white border-gray-200'}
                    `}>
                        <h3 className="text-lg font-semibold text-gray-800 dark:text-white mb-4">Quick Actions</h3>

                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                            <ActionButton to="/products" icon={Package} gradientFrom="from-blue-600" gradientTo="to-cyan-600">
                                View All Products
                            </ActionButton>

                            <ActionButton to="/products/add" icon={PlusCircle} gradientFrom="from-green-600" gradientTo="to-emerald-600">
                                Add New Product
                            </ActionButton>

                            <ActionButton to="/reports" icon={Download} gradientFrom="from-purple-600" gradientTo="to-pink-600">
                                Export Reports
                            </ActionButton>

                            <ActionButton to="/settings" icon={Settings} gradientFrom="from-gray-600" gradientTo="to-slate-600">
                                Settings
                            </ActionButton>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}

export default AdminDashboardPage;
