// client/src/pages/Client/ClientDashboard.jsx
import React from 'react';
import { Link } from 'react-router-dom';

function ClientDashboard({ showTimedMessage }) {
    return (
        <div className="p-8 bg-white rounded-xl shadow-lg dark:bg-gray-800 transition-colors duration-300">
            <h2 className="text-3xl font-bold text-gray-800 dark:text-white mb-6">Client Dashboard</h2>
            <p className="text-gray-700 dark:text-gray-300 mb-6">
                Welcome to your client portal! Here you can view available products and your order history.
            </p>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {/* View Products */}
                <div className="bg-blue-50 p-6 rounded-lg shadow-md dark:bg-gray-700">
                    <h3 className="text-xl font-semibold text-blue-700 dark:text-blue-300 mb-3">Browse Products</h3>
                    <p className="text-gray-700 text-sm dark:text-gray-300 mb-3">
                        Explore the range of products available in our inventory.
                    </p>
                    <Link to="/products" className="inline-block bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600 transition-colors duration-200">
                        View Products
                    </Link>
                </div>

                {/* Order History (Future) */}
                <div className="bg-yellow-50 p-6 rounded-lg shadow-md dark:bg-gray-700">
                    <h3 className="text-xl font-semibold text-yellow-700 dark:text-yellow-300 mb-3">Order History</h3>
                    <p className="text-gray-700 text-sm dark:text-gray-300">
                        (Coming Soon) Track your past orders and their statuses.
                    </p>
                </div>
            </div>
        </div>
    );
}

export default ClientDashboard;
