import React, {
  createContext,
  useState,
  useEffect,
  useContext,
  useCallback,
} from 'react';
import API from '../api/axios'; // Your configured Axios instance

const AuthContext = createContext(null);

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token') || null);
  const [authError, setAuthError] = useState(null);
  const [loadingAuthState, setLoadingAuthState] = useState(true);
  const [authActionInProgress, setAuthActionInProgress] = useState(false);

  // Debugging: Log token state on component mount/update
  useEffect(() => {
    console.log('AuthContext mounted/updated. Current token state:', token ? 'Token present' : 'No token');
    if (token) {
      // Also set the default header for Axios immediately if token exists
      API.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    }
  }, [token]);


  const logout = useCallback(() => {
    console.log('Logging out...');
    setUser(null);
    setToken(null);
    setAuthError(null);
    localStorage.removeItem('token');
    delete API.defaults.headers.common['Authorization'];
    console.log('Logout complete. Token removed.');
  }, []);

  const fetchUserProfile = useCallback(async () => {
    console.log('fetchUserProfile called. Token:', token); // Debugging
    if (!token) {
      console.log('No token in fetchUserProfile, setting loadingAuthState to false.'); // Debugging
      setLoadingAuthState(false);
      return;
    }

    try {
      // Ensure the header is set for this specific request, though interceptor should handle
      API.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      console.log('Attempting to fetch profile with token:', token.substring(0, 10) + '...'); // Debugging: partial token
      const response = await API.get('/auth/profile');
      setUser(response.data);
      setAuthError(null);
      console.log('User profile fetched successfully:', response.data.name); // Debugging
    } catch (error) {
      console.error('ðŸ” Auth failed (fetchUserProfile):', error.response?.data?.message || error.message);
      logout();
      setAuthError(error.response?.data?.message || 'Session expired or unauthorized.');
    } finally {
      setLoadingAuthState(false);
      console.log('fetchUserProfile finished. loadingAuthState set to false.'); // Debugging
    }
  }, [token, logout]);

  useEffect(() => {
    fetchUserProfile();
  }, [fetchUserProfile]);


  const login = async (email, password) => {
    setAuthActionInProgress(true);
    setAuthError(null);
    try {
      const res = await API.post('/auth/login', { email, password });
      const { token: newToken, ...userData } = res.data;
      setToken(newToken);
      setUser(userData);
      localStorage.setItem('token', newToken);
      API.defaults.headers.common['Authorization'] = `Bearer ${newToken}`;
      console.log('Login successful. New token set:', newToken.substring(0, 10) + '...'); // Debugging
      return { success: true };
    } catch (error) {
      const message = error.response?.data?.message || 'Login failed. Please check your credentials.';
      setAuthError(message);
      console.error('Login failed:', message); // Debugging
      return { success: false, message };
    } finally {
      setAuthActionInProgress(false);
    }
  };

  const register = async (name, email, password, role = 'client') => {
    setAuthActionInProgress(true);
    setAuthError(null);
    try {
      const res = await API.post('/auth/register', { name, email, password, role });
      const { token: newToken, ...userData } = res.data;
      setToken(newToken);
      setUser(userData);
      localStorage.setItem('token', newToken);
      API.defaults.headers.common['Authorization'] = `Bearer ${newToken}`;
      console.log('Registration successful. New token set:', newToken.substring(0, 10) + '...'); // Debugging
      return { success: true };
    } catch (error) {
      const message = error.response?.data?.message || 'Registration failed. Please try again with different details.';
      setAuthError(message);
      console.error('Registration failed:', message); // Debugging
      return { success: false, message };
    } finally {
      setAuthActionInProgress(false);
    }
  };

  const value = {
    user,
    token,
    authError,
    isAuthenticated: !!user && !!token,
    isAdmin: user?.role === 'admin',
    isClient: user?.role === 'client',
    loadingAuthState,
    authActionInProgress,
    login,
    register,
    logout,
  };

  return (
    <AuthContext.Provider value={value}>
      {!loadingAuthState && children}
    </AuthContext.Provider>
  );
};
