// client/src/components/AddEditProductModal.jsx

import React, { useState, useEffect } from 'react';
import { X, Save, PlusCircle, Edit, Eye } from 'lucide-react'; // Added Eye icon
import { useTheme } from '../context/ThemeContext.jsx';

function AddEditProductModal({ isOpen, onClose, product, onSave, isViewMode }) {
    const { theme } = useTheme();

    // State for form fields
    const [name, setName] = useState('');
    const [sku, setSku] = useState('');
    const [category, setCategory] = useState('');
    const [stock, setStock] = useState('');
    const [price, setPrice] = useState('');
    const [supplier, setSupplier] = useState('');

    // Populate form fields when product prop changes (for editing/viewing)
    useEffect(() => {
        if (product) {
            setName(product.name || '');
            setSku(product.sku || '');
            setCategory(product.category || '');
            setStock(product.stock || '');
            setPrice(product.price || '');
            setSupplier(product.supplier || '');
        } else {
            // Clear form fields when adding a new product
            setName('');
            setSku('');
            setCategory('');
            setStock('');
            setPrice('');
            setSupplier('');
        }
    }, [product]);

    // Close modal if not open
    if (!isOpen) return null;

    const handleSubmit = (e) => {
        e.preventDefault();
        // If in view mode, prevent submission
        if (isViewMode) {
            onClose(); // Just close the modal
            return;
        }

        // Basic validation for add/edit mode
        if (!name || !sku || !category || stock === '' || price === '' || !supplier) {
            alert('Please fill in all fields.'); // Consider using useNotification here instead of alert
            return;
        }

        const productData = {
            id: product ? product.id : null, // Keep existing ID for edit, null for new
            name,
            sku,
            category,
            stock: parseInt(stock),
            price: parseFloat(price),
            supplier,
            lastUpdated: new Date().toISOString().split('T')[0] // Current date
        };
        onSave(productData); // Call the onSave function passed from parent
    };

    // Dynamic titles and button texts based on mode
    const modalTitle = isViewMode ? 'Product Details' : (product ? 'Edit Product' : 'Add New Product');
    const submitButtonText = isViewMode ? 'Close' : (product ? 'Save Changes' : 'Add Product');
    const submitButtonIcon = isViewMode ? X : (product ? Edit : PlusCircle); // Icon changes based on mode

    return (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50 backdrop-blur-sm p-4">
            <div className={`relative w-full max-w-md rounded-2xl shadow-2xl p-6 transform transition-all duration-300 ease-out
                ${theme === 'dark' ? 'bg-gray-800 text-white border border-gray-700' : 'bg-white text-gray-900 border border-gray-200'}
                scale-100 opacity-100
            `}>
                <div className="flex justify-between items-center mb-6">
                    <h2 className="text-2xl font-bold text-blue-600 dark:text-blue-300">{modalTitle}</h2>
                    <button
                        onClick={onClose}
                        className="p-2 rounded-full text-gray-600 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700 transition-colors"
                        title="Close"
                    >
                        <X className="w-6 h-6" />
                    </button>
                </div>

                <form onSubmit={handleSubmit} className="space-y-4">
                    <div>
                        <label htmlFor="name" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Product Name</label>
                        <input
                            type="text"
                            id="name"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            className={`mt-1 block w-full p-3 border rounded-lg focus:ring-blue-500 focus:border-blue-500
                                ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-gray-100 border-gray-300 text-gray-900'}
                            `}
                            required
                            readOnly={isViewMode} // Make read-only in view mode
                        />
                    </div>
                    <div>
                        <label htmlFor="sku" className="block text-sm font-medium text-gray-700 dark:text-gray-300">SKU</label>
                        <input
                            type="text"
                            id="sku"
                            value={sku}
                            onChange={(e) => setSku(e.target.value)}
                            className={`mt-1 block w-full p-3 border rounded-lg focus:ring-blue-500 focus:border-blue-500
                                ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-gray-100 border-gray-300 text-gray-900'}
                            `}
                            required
                            readOnly={isViewMode} // Make read-only in view mode
                        />
                    </div>
                    <div>
                        <label htmlFor="category" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Category</label>
                        <input
                            type="text"
                            id="category"
                            value={category}
                            onChange={(e) => setCategory(e.target.value)}
                            className={`mt-1 block w-full p-3 border rounded-lg focus:ring-blue-500 focus:border-blue-500
                                ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-gray-100 border-gray-300 text-gray-900'}
                            `}
                            required
                            readOnly={isViewMode} // Make read-only in view mode
                        />
                    </div>
                    <div className="grid grid-cols-2 gap-4">
                        <div>
                            <label htmlFor="stock" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Stock</label>
                            <input
                                type="number"
                                id="stock"
                                value={stock}
                                onChange={(e) => setStock(e.target.value)}
                                className={`mt-1 block w-full p-3 border rounded-lg focus:ring-blue-500 focus:border-blue-500
                                    ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-gray-100 border-gray-300 text-gray-900'}
                                `}
                                required
                                readOnly={isViewMode} // Make read-only in view mode
                            />
                        </div>
                        <div>
                            <label htmlFor="price" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Price</label>
                            <input
                                type="number"
                                id="price"
                                value={price}
                                onChange={(e) => setPrice(e.target.value)}
                                step="0.01"
                                className={`mt-1 block w-full p-3 border rounded-lg focus:ring-blue-500 focus:border-blue-500
                                    ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-gray-100 border-gray-300 text-gray-900'}
                                `}
                                required
                                readOnly={isViewMode} // Make read-only in view mode
                            />
                        </div>
                    </div>
                    <div>
                        <label htmlFor="supplier" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Supplier</label>
                        <input
                            type="text"
                            id="supplier"
                            value={supplier}
                            onChange={(e) => setSupplier(e.target.value)}
                            className={`mt-1 block w-full p-3 border rounded-lg focus:ring-blue-500 focus:border-blue-500
                                ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-gray-100 border-gray-300 text-gray-900'}
                            `}
                            required
                            readOnly={isViewMode} // Make read-only in view mode
                        />
                    </div>

                    <button
                        type="submit"
                        className="w-full bg-gradient-to-r from-blue-600 to-cyan-600 text-white py-3 px-6 rounded-xl font-semibold hover:from-blue-700 hover:to-cyan-700 transform hover:scale-105 transition-all duration-200 shadow-lg flex items-center justify-center gap-2"
                    >
                        {React.createElement(submitButtonIcon, { className: "w-5 h-5" })}
                        {submitButtonText}
                    </button>
                </form>
            </div>
        </div>
    );
}

export default AddEditProductModal;
