// client/src/auth/AuthContext.jsx

import React, { createContext, useState, useEffect, useContext, useCallback } from 'react';
import axios from 'axios'; // Use plain axios here, not your custom API instance
import { useNotification } from '../components/Notification.jsx';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [token, setToken] = useState(localStorage.getItem('token') || null);
    const [isAuthenticated, setIsAuthenticated] = useState(!!token);
    const [loadingAuthState, setLoadingAuthState] = useState(true);
    const { showTimedMessage } = useNotification();

    // Create an Axios instance specifically for authenticated requests
    // This instance will have interceptors to manage the token
    const authAxios = axios.create({
        baseURL: 'http://localhost:5000/api', // Your backend API base URL
        headers: {
            'Content-Type': 'application/json',
        },
    });

    // Request Interceptor: Attach the latest token to every outgoing request
    authAxios.interceptors.request.use(
        (config) => {
            const currentToken = localStorage.getItem('token'); // Always get the freshest token from localStorage
            if (currentToken) {
                config.headers.Authorization = `Bearer ${currentToken}`;
            }
            return config;
        },
        (error) => {
            return Promise.reject(error);
        }
    );

    // Response Interceptor: Handle 401 Unauthorized errors globally
    const logout = useCallback(() => {
        localStorage.removeItem('token');
        setToken(null);
        setUser(null);
        setIsAuthenticated(false);
        showTimedMessage('Logged out successfully.', 3000, 'info');
    }, [showTimedMessage]);

    authAxios.interceptors.response.use(
        (response) => response,
        (error) => {
            if (error.response && error.response.status === 401) {
                console.error('ðŸ” Auth failed (Interceptor): Unauthorized or invalid token. Logging out.');
                logout(); // Log out user on 401
                showTimedMessage('Session expired or unauthorized. Please log in again.', 5000, 'error');
            }
            return Promise.reject(error);
        }
    );

    // Function to fetch user profile
    const fetchUserProfile = useCallback(async (authToken) => {
        if (!authToken) {
            setUser(null);
            setIsAuthenticated(false);
            setLoadingAuthState(false);
            return;
        }
        try {
            // Use authAxios here, as it will automatically attach the token
            const response = await authAxios.get('/auth/profile');
            setUser(response.data);
            setIsAuthenticated(true);
            console.log('âœ… User profile fetched:', response.data.email);
        } catch (error) {
            // Error handling for fetchUserProfile is now largely handled by the interceptor
            // But we keep this for initial setup or if interceptor somehow misses it.
            console.error('ðŸ” Auth failed (fetchUserProfile):', error.response?.status, error.response?.data?.message || error.message);
            setUser(null);
            setIsAuthenticated(false);
            localStorage.removeItem('token'); // Ensure token is cleared
            setToken(null);
        } finally {
            setLoadingAuthState(false);
        }
    }, [logout, authAxios]); // Depend on logout and authAxios

    // Login function
    const login = useCallback(async (email, password) => {
        setLoadingAuthState(true); // Set loading true during login attempt
        try {
            const response = await axios.post('http://localhost:5000/api/auth/login', { email, password });
            const newToken = response.data.token;
            localStorage.setItem('token', newToken); // Store token
            setToken(newToken); // Update state
            setIsAuthenticated(true); // Set authenticated immediately
            await fetchUserProfile(newToken); // Fetch user profile
            showTimedMessage('Login successful!', 3000, 'success');
            return true;
        } catch (error) {
            console.error('âŒ Login failed:', error.response?.data?.message || error.message);
            showTimedMessage(error.response?.data?.message || 'Login failed.', 5000, 'error');
            setLoadingAuthState(false); // Reset loading on login failure
            return false;
        }
    }, [fetchUserProfile, showTimedMessage]);

    // Initial check on component mount/token change
    useEffect(() => {
        if (token) {
            fetchUserProfile(token);
        } else {
            setLoadingAuthState(false); // No token, so no auth state to load
        }
    }, [token, fetchUserProfile]); // Re-run if token changes or fetchUserProfile changes

    const value = {
        user,
        token,
        isAuthenticated,
        loadingAuthState,
        login,
        logout,
        authAxios, // EXPOSE THE AUTHENTICATED AXIOS INSTANCE
        // No longer need authError or authActionInProgress if not used elsewhere
        isAdmin: user?.role === 'admin', // Add isAdmin helper
        isClient: user?.role === 'client', // Add isClient helper
    };

    return (
        <AuthContext.Provider value={value}>
            {/* Render children only when auth state is done loading to prevent flickering */}
            {!loadingAuthState && children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
};
