import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../auth/AuthContext.jsx';
import { useTheme } from '../../context/ThemeContext.jsx';
import { useNotification } from '../../components/Notification.jsx';
import API from '../../api/axios.js';
import { User, Mail, Lock, Settings, Loader2, Save } from 'lucide-react';

function ProfilePage() {
    const { isAuthenticated, loading: authLoading, user, token, updateUser } = useAuth(); // Get updateUser from AuthContext
    const { theme } = useTheme();
    const { showTimedMessage } = useNotification();
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        name: '',
        email: '',
        address: '',
        password: '',
        confirmPassword: '',
        // New notification preferences
        notificationPreferences: {
            receiveLowStockAlerts: false,
            lowStockAlertEmail: '',
        },
    });
    const [loadingProfile, setLoadingProfile] = useState(true);
    const [isSaving, setIsSaving] = useState(false);
    const [errors, setErrors] = useState({});

    // Fetch user profile data on component mount
    useEffect(() => {
        const fetchProfile = async () => {
            if (!isAuthenticated || !token) {
                setLoadingProfile(false);
                navigate('/login');
                return;
            }

            try {
                const response = await API.get('/users/profile', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                const userData = response.data;
                setFormData({
                    name: userData.name || '',
                    email: userData.email || '',
                    address: userData.address || '',
                    password: '', // Never pre-fill password
                    confirmPassword: '', // Never pre-fill confirm password
                    notificationPreferences: {
                        receiveLowStockAlerts: userData.notificationPreferences?.receiveLowStockAlerts || false,
                        lowStockAlertEmail: userData.notificationPreferences?.lowStockAlertEmail || userData.email || '',
                    },
                });
                setLoadingProfile(false);
            } catch (err) {
                console.error('Failed to fetch profile:', err);
                showTimedMessage(err.response?.data?.message || 'Failed to load profile.', 5000, 'error');
                setLoadingProfile(false);
                navigate('/login'); // Redirect on profile fetch failure
            }
        };

        if (!authLoading) {
            fetchProfile();
        }
    }, [isAuthenticated, authLoading, token, navigate, showTimedMessage]);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;

        if (name.startsWith('notificationPreferences.')) {
            const prefName = name.split('.')[1];
            setFormData(prev => ({
                ...prev,
                notificationPreferences: {
                    ...prev.notificationPreferences,
                    [prefName]: type === 'checkbox' ? checked : value
                }
            }));
        } else {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsSaving(true);
        setErrors({});

        if (formData.password !== formData.confirmPassword) {
            setErrors({ confirmPassword: 'Passwords do not match.' });
            setIsSaving(false);
            return;
        }

        try {
            const payload = {
                name: formData.name,
                email: formData.email,
                address: formData.address,
                notificationPreferences: formData.notificationPreferences, // Include notification preferences
            };

            if (formData.password) { // Only send password if it's being changed
                payload.password = formData.password;
            }

            // Call the updateUser function from AuthContext
            // This function should handle the API call to /api/users/profile on the backend
            await updateUser(payload);
            showTimedMessage('Profile updated successfully!', 3000, 'success');
            setFormData(prev => ({ ...prev, password: '', confirmPassword: '' })); // Clear passwords after update
        } catch (err) {
            console.error('Failed to update profile:', err);
            const apiErrors = err.response?.data?.message;
            if (apiErrors) {
                if (typeof apiErrors === 'string') {
                    setErrors({ general: apiErrors });
                } else if (Array.isArray(apiErrors)) {
                    setErrors({ general: apiErrors.join(', ') });
                } else if (typeof apiErrors === 'object') {
                    setErrors(apiErrors); // Assuming API returns field-specific errors
                }
            } else {
                setErrors({ general: 'Failed to update profile. Please try again.' });
            }
            showTimedMessage(apiErrors || 'Failed to update profile.', 5000, 'error');
        } finally {
            setIsSaving(false);
        }
    };

    if (authLoading || loadingProfile) {
        return (
            <div className="flex items-center justify-center min-h-screen bg-gray-100 dark:bg-gray-900 transition-colors duration-300">
                <div className="flex items-center space-x-3 text-lg font-medium text-blue-600 dark:text-blue-300">
                    <div className="w-8 h-8 border-4 border-blue-500 border-t-transparent rounded-full animate-spin"></div>
                    <span>Loading profile...</span>
                </div>
            </div>
        );
    }

    if (!isAuthenticated) {
        return (
            <div className="flex items-center justify-center min-h-screen bg-gray-100 dark:bg-gray-900 transition-colors duration-300">
                <p className="text-red-600 dark:text-red-300">You need to be logged in to view your profile.</p>
            </div>
        );
    }

    return (
        <div className={`relative p-6 rounded-xl shadow-lg transition-colors duration-300
            ${theme === 'dark' ? 'bg-gradient-to-br from-slate-900 via-blue-900 to-slate-800' : 'bg-white'}
            ${theme === 'dark' ? 'shadow-2xl' : 'shadow-lg'}
        `}>
            {theme === 'dark' && (
                <div className="absolute inset-0 opacity-20 rounded-xl" style={{
                    backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23ffffff' fill-opacity='0.03'%3E%3Cpath d='M36 34v-4h-2v4h-4v2h4v4h2v-4h4v-2h-4zm0-30V0h-2v4h-4v2h4v4h2V6h4V4h-4zM6 34v-4H4v4H0v2h4v4h2v-4h4v-2H6zM6 4V0H4v4H0v2h4v4h2V6h4V4H6z'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")`
                }}></div>
            )}

            <div className="relative z-10">
                <div className={`flex flex-col md:flex-row justify-between items-start md:items-center mb-6 p-4 rounded-lg border transition-colors duration-300
                    ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-blue-50 border-blue-200'}
                `}>
                    <div className="flex items-center space-x-4">
                        <div className="bg-gradient-to-r from-blue-500 to-cyan-500 p-3 rounded-xl shadow-lg">
                            <User className="w-8 h-8 text-white" />
                        </div>
                        <div>
                            <h1 className="text-3xl font-bold text-blue-800 dark:text-white">My Profile</h1>
                            <p className="text-blue-700 mt-1 dark:text-blue-200">Manage your account information.</p>
                        </div>
                    </div>
                    <div className="flex items-center gap-3 mt-4 md:mt-0">
                        {/* Add any profile-related buttons here if needed */}
                    </div>
                </div>

                {errors.general && (
                    <div className="p-4 mb-6 bg-red-100 dark:bg-red-900 border border-red-400 dark:border-red-700 text-red-700 dark:text-red-300 rounded-lg text-center">
                        <p>{errors.general}</p>
                    </div>
                )}

                <form onSubmit={handleSubmit} className="space-y-6">
                    {/* Basic Information Section */}
                    <div className={`p-6 rounded-xl shadow-md border transition-colors duration-300
                        ${theme === 'dark' ? 'bg-white bg-opacity-10 border-white border-opacity-20' : 'bg-white border-gray-200'}
                    `}>
                        <h2 className="text-xl font-semibold mb-4 text-gray-800 dark:text-white flex items-center">
                            <User className="w-5 h-5 mr-2 text-blue-500" /> Basic Information
                        </h2>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            <div>
                                <label htmlFor="name" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Name</label>
                                <input
                                    type="text"
                                    id="name"
                                    name="name"
                                    value={formData.name}
                                    onChange={handleChange}
                                    className={`mt-1 block w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500
                                        ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-gray-50 border-gray-300 text-gray-900'}
                                    `}
                                    required
                                />
                                {errors.name && <p className="text-red-500 text-xs mt-1">{errors.name}</p>}
                            </div>
                            <div>
                                <label htmlFor="email" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Email Address</label>
                                <input
                                    type="email"
                                    id="email"
                                    name="email"
                                    value={formData.email}
                                    onChange={handleChange}
                                    className={`mt-1 block w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500
                                        ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-gray-50 border-gray-300 text-gray-900'}
                                    `}
                                    required
                                />
                                {errors.email && <p className="text-red-500 text-xs mt-1">{errors.email}</p>}
                            </div>
                            <div className="md:col-span-2">
                                <label htmlFor="address" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Address</label>
                                <textarea
                                    id="address"
                                    name="address"
                                    value={formData.address}
                                    onChange={handleChange}
                                    rows="2"
                                    className={`mt-1 block w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500
                                        ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-gray-50 border-gray-300 text-gray-900'}
                                    `}
                                ></textarea>
                                {errors.address && <p className="text-red-500 text-xs mt-1">{errors.address}</p>}
                            </div>
                        </div>
                    </div>

                    {/* Password Section */}
                    <div className={`p-6 rounded-xl shadow-md border transition-colors duration-300
                        ${theme === 'dark' ? 'bg-white bg-opacity-10 border-white border-opacity-20' : 'bg-white border-gray-200'}
                    `}>
                        <h2 className="text-xl font-semibold mb-4 text-gray-800 dark:text-white flex items-center">
                            <Lock className="w-5 h-5 mr-2 text-blue-500" /> Change Password
                        </h2>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            <div>
                                <label htmlFor="password" className="block text-sm font-medium text-gray-700 dark:text-gray-300">New Password</label>
                                <input
                                    type="password"
                                    id="password"
                                    name="password"
                                    value={formData.password}
                                    onChange={handleChange}
                                    className={`mt-1 block w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500
                                        ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-gray-50 border-gray-300 text-gray-900'}
                                    `}
                                    placeholder="Leave blank to keep current password"
                                />
                                {errors.password && <p className="text-red-500 text-xs mt-1">{errors.password}</p>}
                            </div>
                            <div>
                                <label htmlFor="confirmPassword" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Confirm New Password</label>
                                <input
                                    type="password"
                                    id="confirmPassword"
                                    name="confirmPassword"
                                    value={formData.confirmPassword}
                                    onChange={handleChange}
                                    className={`mt-1 block w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500
                                        ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-gray-50 border-gray-300 text-gray-900'}
                                    `}
                                />
                                {errors.confirmPassword && <p className="text-red-500 text-xs mt-1">{errors.confirmPassword}</p>}
                            </div>
                        </div>
                    </div>

                    {/* Notification Preferences Section (NEW) */}
                    <div className={`p-6 rounded-xl shadow-md border transition-colors duration-300
                        ${theme === 'dark' ? 'bg-white bg-opacity-10 border-white border-opacity-20' : 'bg-white border-gray-200'}
                    `}>
                        <h2 className="text-xl font-semibold mb-4 text-gray-800 dark:text-white flex items-center">
                            <Settings className="w-5 h-5 mr-2 text-blue-500" /> Notification Preferences
                        </h2>
                        <div className="space-y-4">
                            <div className="flex items-center">
                                <input
                                    type="checkbox"
                                    id="receiveLowStockAlerts"
                                    name="notificationPreferences.receiveLowStockAlerts"
                                    checked={formData.notificationPreferences.receiveLowStockAlerts}
                                    onChange={handleChange}
                                    className={`h-4 w-4 rounded border-gray-300 text-blue-600 focus:ring-blue-500
                                        ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : ''}
                                    `}
                                />
                                <label htmlFor="receiveLowStockAlerts" className="ml-2 block text-sm font-medium text-gray-700 dark:text-gray-300">
                                    Receive Low Stock Alerts
                                </label>
                            </div>
                            {formData.notificationPreferences.receiveLowStockAlerts && (
                                <div>
                                    <label htmlFor="lowStockAlertEmail" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Alert Email Address</label>
                                    <input
                                        type="email"
                                        id="lowStockAlertEmail"
                                        name="notificationPreferences.lowStockAlertEmail"
                                        value={formData.notificationPreferences.lowStockAlertEmail}
                                        onChange={handleChange}
                                        className={`mt-1 block w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500
                                            ${theme === 'dark' ? 'bg-gray-700 border-gray-600 text-white' : 'bg-gray-50 border-gray-300 text-gray-900'}
                                        `}
                                        required={formData.notificationPreferences.receiveLowStockAlerts}
                                    />
                                    {errors['notificationPreferences.lowStockAlertEmail'] && <p className="text-red-500 text-xs mt-1">{errors['notificationPreferences.lowStockAlertEmail']}</p>}
                                    <p className="text-gray-500 dark:text-gray-400 text-xs mt-1">Notifications will be sent to this email address.</p>
                                </div>
                            )}
                        </div>
                    </div>

                    {/* Submit Button */}
                    <div className="flex justify-end mt-6">
                        <button
                            type="submit"
                            disabled={isSaving}
                            className={`inline-flex items-center px-6 py-3 border border-transparent text-base font-medium rounded-xl shadow-sm transition-all duration-200
                                ${isSaving
                                    ? 'bg-gray-400 text-gray-700 cursor-not-allowed'
                                    : 'bg-gradient-to-r from-blue-600 to-indigo-700 text-white hover:from-blue-700 hover:to-indigo-800 transform hover:scale-105'
                                }
                            `}
                        >
                            {isSaving ? (
                                <>
                                    <Loader2 className="w-5 h-5 mr-2 animate-spin" /> Saving...
                                </>
                            ) : (
                                <>
                                    <Save className="w-5 h-5 mr-2" /> Save Profile
                                </>
                            )}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default ProfilePage;
