// client/src/pages/Products/ProductsPage.jsx

import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../../auth/AuthContext.jsx';
import { useTheme } from '../../context/ThemeContext.jsx';
import { useNotification } from '../../components/Notification.jsx';
import API from '../../api/axios.js'; // Your configured Axios instance
import { Link, useNavigate } from 'react-router-dom';
import {
    PlusCircle, Search, RefreshCw, Edit, Trash2, Loader2,
    ChevronLeft, ChevronRight, ChevronsLeft, ChevronsRight, Package
} from 'lucide-react';

const ProductsPage = () => {
    const { isAuthenticated, loadingAuthState } = useAuth();
    const { theme } = useTheme();
    const { showTimedMessage } = useNotification();
    const navigate = useNavigate();

    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');
    const [currentPage, setCurrentPage] = useState(1);
    const [productsPerPage] = useState(10); // Number of products to display per page
    const [isRefreshing, setIsRefreshing] = useState(false);
    const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
    const [productToDelete, setProductToDelete] = useState(null);

    const fetchProducts = useCallback(async () => {
        setLoading(true);
        setError(null);
        setIsRefreshing(true);
        try {
            const response = await API.get('/products'); // This endpoint populates category name
            if (!Array.isArray(response.data)) {
                console.error('API response is not an array:', response.data);
                setProducts([]);
                showTimedMessage('Received invalid product data format.', 3000, 'error');
            } else {
                setProducts(response.data);
                showTimedMessage('Products loaded successfully!', 2000, 'success');
            }
        } catch (err) {
            console.error('Failed to fetch products:', err);
            setError('Failed to load products. Please try again.');
            showTimedMessage('Failed to load products.', 5000, 'error');
        } finally {
            setLoading(false);
            setIsRefreshing(false);
        }
    }, [showTimedMessage]);

    useEffect(() => {
        if (!loadingAuthState && !isAuthenticated) {
            navigate('/login');
        } else if (isAuthenticated) {
            fetchProducts();
        }
    }, [isAuthenticated, loadingAuthState, navigate, fetchProducts]);

    const handleDeleteClick = (product) => {
        setProductToDelete(product);
        setShowDeleteConfirm(true);
    };

    const confirmDelete = async () => {
        if (!productToDelete) return;

        try {
            await API.delete(`/products/${productToDelete._id}`);
            showTimedMessage('Product deleted successfully!', 2000, 'success');
            setProducts(products.filter(p => p._id !== productToDelete._id)); // Update state
        } catch (err) {
            console.error('Error deleting product:', err);
            showTimedMessage('Failed to delete product.', 5000, 'error');
        } finally {
            setShowDeleteConfirm(false);
            setProductToDelete(null);
        }
    };

    const cancelDelete = () => {
        setShowDeleteConfirm(false);
        setProductToDelete(null);
    };

    const filteredProducts = products.filter(product =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.sku.toLowerCase().includes(searchTerm.toLowerCase()) ||
        // Access category.name for filtering
        (product.category && product.category.name.toLowerCase().includes(searchTerm.toLowerCase())) ||
        product.supplier.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Pagination logic
    const indexOfLastProduct = currentPage * productsPerPage;
    const indexOfFirstProduct = indexOfLastProduct - productsPerPage;
    const currentProducts = filteredProducts.slice(indexOfFirstProduct, indexOfLastProduct);
    const totalPages = Math.ceil(filteredProducts.length / productsPerPage);

    const paginate = (pageNumber) => setCurrentPage(pageNumber);

    if (loadingAuthState) {
        return (
            <div className="flex items-center justify-center min-h-screen bg-gray-100 dark:bg-gray-900 transition-colors duration-300">
                <div className="w-8 h-8 border-4 border-blue-500 border-t-transparent rounded-full animate-spin"></div>
                <span className="ml-3 text-lg font-medium text-blue-600 dark:text-blue-300">Loading application...</span>
            </div>
        );
    }

    if (!isAuthenticated) {
        return null; // Redirect handled by useEffect
    }

    return (
        <div className={`relative p-6 rounded-xl shadow-lg transition-colors duration-300 mt-6
            ${theme === 'dark' ? 'bg-gradient-to-br from-slate-900 via-blue-900 to-slate-800' : 'bg-white'}
            ${theme === 'dark' ? 'shadow-2xl' : 'shadow-lg'}
        `}>
            {theme === 'dark' && (
                <div className="absolute inset-0 opacity-20 rounded-xl" style={{
                    backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23ffffff' fill-opacity='0.03'%3E%3Cpath d='M36 34v-4h-2v4h-4v2h4v4h2v-4h4v-2h-4zm0-30V0h-2v4h-4v2h4v4h2V6h4V4h-4zM6 34v-4H4v4H0v2h4v4h2v-4h4v-2H6zM6 4V0H4v4H0v2h4v4h2V6h4V4H6z'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")`
                }}></div>
            )}

            <div className="relative z-10">
                <div className={`flex flex-col md:flex-row justify-between items-start md:items-center mb-6 p-4 rounded-lg border transition-colors duration-300
                    ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-blue-50 border-blue-200'}
                `}>
                    <div className="flex items-center space-x-4">
                        <div className="bg-gradient-to-r from-blue-500 to-cyan-500 p-3 rounded-xl shadow-lg">
                            <Package className="w-8 h-8 text-white" />
                        </div>
                        <div>
                            <h1 className="text-3xl font-bold text-blue-800 dark:text-white">
                                All Products
                            </h1>
                            <p className="text-blue-700 mt-1 dark:text-blue-200">
                                Manage your inventory products.
                            </p>
                        </div>
                    </div>

                    <div className="flex items-center gap-3 mt-4 md:mt-0">
                        <Link to="/products/add" className="bg-gradient-to-r from-green-500 to-emerald-500 text-white px-5 py-2 rounded-xl shadow-lg hover:shadow-xl transform hover:scale-105 transition-all duration-200 text-sm font-semibold flex items-center gap-2">
                            <PlusCircle className="w-4 h-4" /> Add Product
                        </Link>
                        <button
                            onClick={fetchProducts}
                            disabled={isRefreshing}
                            className={`p-2 rounded-xl border transition-all duration-200 ${isRefreshing ? 'animate-spin' : ''}
                                ${theme === 'dark' ? 'bg-gray-800 border-gray-600 hover:bg-gray-700' : 'bg-gray-100 border-gray-300 hover:bg-gray-200'}
                                disabled:opacity-60 disabled:cursor-not-allowed
                            `}
                        >
                            <RefreshCw className="w-5 h-5 text-gray-600 dark:text-blue-200" />
                        </button>
                        <div className={`flex items-center gap-2 rounded-xl px-4 py-2 border transition-colors duration-300
                            ${theme === 'dark' ? 'bg-gray-800 border-gray-600' : 'bg-gray-100 border-gray-300'}
                        `}>
                            <Search className="w-4 h-4 text-gray-600 dark:text-blue-200" />
                            <input
                                type="text"
                                placeholder="Search products..."
                                value={searchTerm}
                                onChange={(e) => setSearchTerm(e.target.value)}
                                className="bg-transparent border-none outline-none text-sm text-gray-800 placeholder-gray-500 dark:text-white dark:placeholder-blue-300 w-48"
                            />
                        </div>
                    </div>
                </div>

                {error && (
                    <div className="p-4 mb-6 bg-red-100 dark:bg-red-900 border border-red-400 dark:border-red-700 text-red-700 dark:text-red-300 rounded-lg text-center">
                        <p>{error}</p>
                    </div>
                )}

                {loading ? (
                    <div className="flex items-center justify-center p-10">
                        <Loader2 className="w-8 h-8 animate-spin text-blue-500" />
                        <span className="ml-3 text-lg text-gray-600 dark:text-blue-200">Loading products...</span>
                    </div>
                ) : products.length === 0 ? (
                    <div className={`p-10 text-center rounded-xl border ${theme === 'dark' ? 'bg-gray-800 border-gray-700 text-gray-300' : 'bg-gray-100 border-gray-200 text-gray-600'}`}>
                        <p className="text-xl font-semibold mb-2">No products found.</p>
                        <p>Start by adding a new product.</p>
                        <Link to="/products/add" className="mt-4 inline-flex items-center px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors">
                            <PlusCircle className="w-4 h-4 mr-2" /> Add New Product
                        </Link>
                    </div>
                ) : (
                    <>
                        <div className="overflow-x-auto rounded-xl shadow-md">
                            <table className="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
                                <thead className={`
                                    ${theme === 'dark' ? 'bg-gray-800' : 'bg-gray-100'}
                                    `}>
                                    <tr>
                                        <th className={`px-6 py-3 text-left text-xs font-medium uppercase tracking-wider
                                            ${theme === 'dark' ? 'text-gray-300' : 'text-gray-500'}
                                        `}>
                                            Product Name
                                        </th>
                                        <th className={`px-6 py-3 text-left text-xs font-medium uppercase tracking-wider
                                            ${theme === 'dark' ? 'text-gray-300' : 'text-gray-500'}
                                        `}>
                                            SKU
                                        </th>
                                        <th className={`px-6 py-3 text-left text-xs font-medium uppercase tracking-wider
                                            ${theme === 'dark' ? 'text-gray-300' : 'text-gray-500'}
                                        `}>
                                            Category
                                        </th>
                                        <th className={`px-6 py-3 text-left text-xs font-medium uppercase tracking-wider
                                            ${theme === 'dark' ? 'text-gray-300' : 'text-gray-500'}
                                        `}>
                                            Stock
                                        </th>
                                        <th className={`px-6 py-3 text-left text-xs font-medium uppercase tracking-wider
                                            ${theme === 'dark' ? 'text-gray-300' : 'text-gray-500'}
                                        `}>
                                            Price
                                        </th>
                                        <th className={`px-6 py-3 text-left text-xs font-medium uppercase tracking-wider
                                            ${theme === 'dark' ? 'text-gray-300' : 'text-gray-500'}
                                        `}>
                                            Supplier
                                        </th>
                                        <th className={`px-6 py-3 text-right text-xs font-medium uppercase tracking-wider
                                            ${theme === 'dark' ? 'text-gray-300' : 'text-gray-500'}
                                        `}>
                                            Actions
                                        </th>
                                    </tr>
                                </thead>
                                <tbody className={`divide-y divide-gray-200 dark:divide-gray-700
                                    ${theme === 'dark' ? 'bg-gray-900' : 'bg-white'}
                                `}>
                                    {currentProducts.map((product) => (
                                        <tr key={product._id} className="hover:bg-gray-50 dark:hover:bg-gray-700 transition-colors">
                                            <td className={`px-6 py-4 whitespace-nowrap text-sm font-medium
                                                ${theme === 'dark' ? 'text-white' : 'text-gray-900'}
                                            `}>
                                                {product.name}
                                            </td>
                                            <td className={`px-6 py-4 whitespace-nowrap text-sm
                                                ${theme === 'dark' ? 'text-gray-300' : 'text-gray-500'}
                                            `}>
                                                {product.sku}
                                            </td>
                                            <td className={`px-6 py-4 whitespace-nowrap text-sm
                                                ${theme === 'dark' ? 'text-gray-300' : 'text-gray-500'}
                                            `}>
                                                {/* Access category.name here */}
                                                {product.category ? product.category.name : 'N/A'}
                                            </td>
                                            <td className={`px-6 py-4 whitespace-nowrap text-sm
                                                ${theme === 'dark' ? 'text-gray-300' : 'text-gray-500'}
                                            `}>
                                                {product.stock}
                                            </td>
                                            <td className={`px-6 py-4 whitespace-nowrap text-sm
                                                ${theme === 'dark' ? 'text-gray-300' : 'text-gray-500'}
                                            `}>
                                                ${product.price ? product.price.toFixed(2) : '0.00'}
                                            </td>
                                            <td className={`px-6 py-4 whitespace-nowrap text-sm
                                                ${theme === 'dark' ? 'text-gray-300' : 'text-gray-500'}
                                            `}>
                                                {product.supplier}
                                            </td>
                                            <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                                                <div className="flex justify-end space-x-2">
                                                    <Link to={`/products/edit/${product._id}`} className="text-blue-600 hover:text-blue-900 dark:text-blue-400 dark:hover:text-blue-200 transition-colors">
                                                        <Edit className="w-5 h-5" />
                                                    </Link>
                                                    <button onClick={() => handleDeleteClick(product)} className="text-red-600 hover:text-red-900 dark:text-red-400 dark:hover:text-red-200 transition-colors">
                                                        <Trash2 className="w-5 h-5" />
                                                    </button>
                                                </div>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>

                        {/* Pagination */}
                        <div className={`flex justify-center items-center space-x-2 mt-6 p-4 rounded-xl
                            ${theme === 'dark' ? 'bg-gray-800 text-gray-300' : 'bg-gray-100 text-gray-700'}
                        `}>
                            <button
                                onClick={() => paginate(1)}
                                disabled={currentPage === 1}
                                className={`p-2 rounded-lg ${currentPage === 1 ? 'opacity-50 cursor-not-allowed' : 'hover:bg-gray-200 dark:hover:bg-gray-700'} transition-colors`}
                            >
                                <ChevronsLeft className="w-5 h-5" />
                            </button>
                            <button
                                onClick={() => paginate(currentPage - 1)}
                                disabled={currentPage === 1}
                                className={`p-2 rounded-lg ${currentPage === 1 ? 'opacity-50 cursor-not-allowed' : 'hover:bg-gray-200 dark:hover:bg-gray-700'} transition-colors`}
                            >
                                <ChevronLeft className="w-5 h-5" />
                            </button>
                            {Array.from({ length: totalPages }, (_, i) => i + 1).map(page => (
                                <button
                                    key={page}
                                    onClick={() => paginate(page)}
                                    className={`px-4 py-2 rounded-lg font-semibold
                                        ${currentPage === page ? 'bg-blue-500 text-white shadow-md' : 'hover:bg-gray-200 dark:hover:bg-gray-700'}
                                        transition-colors
                                    `}
                                >
                                    {page}
                                </button>
                            ))}
                            <button
                                onClick={() => paginate(currentPage + 1)}
                                disabled={currentPage === totalPages}
                                className={`p-2 rounded-lg ${currentPage === totalPages ? 'opacity-50 cursor-not-allowed' : 'hover:bg-gray-200 dark:hover:bg-gray-700'} transition-colors`}
                            >
                                <ChevronRight className="w-5 h-5" />
                            </button>
                            <button
                                onClick={() => paginate(totalPages)}
                                disabled={currentPage === totalPages}
                                className={`p-2 rounded-lg ${currentPage === totalPages ? 'opacity-50 cursor-not-allowed' : 'hover:bg-gray-200 dark:hover:bg-gray-700'} transition-colors`}
                            >
                                <ChevronsRight className="w-5 h-5" />
                            </button>
                        </div>
                    </>
                )}

                {/* Delete Confirmation Modal */}
                {showDeleteConfirm && (
                    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
                        <div className={`p-8 rounded-xl shadow-2xl w-full max-w-md text-center
                            ${theme === 'dark' ? 'bg-gray-800 text-white' : 'bg-white text-gray-900'}
                        `}>
                            <h3 className="text-xl font-bold mb-4">Confirm Deletion</h3>
                            <p className="mb-6">Are you sure you want to delete "<span className="font-semibold">{productToDelete?.name}</span>"? This action cannot be undone.</p>
                            <div className="flex justify-center space-x-4">
                                <button
                                    onClick={cancelDelete}
                                    className={`px-6 py-2 rounded-lg font-semibold border
                                        ${theme === 'dark' ? 'border-gray-600 text-gray-300 hover:bg-gray-700' : 'border-gray-300 text-gray-700 hover:bg-gray-100'}
                                        transition-colors
                                    `}
                                >
                                    Cancel
                                </button>
                                <button
                                    onClick={confirmDelete}
                                    className="px-6 py-2 rounded-lg font-semibold bg-red-600 text-white hover:bg-red-700 transition-colors"
                                >
                                    Delete
                                </button>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default ProductsPage;
